mapper.select:
SELECT d.draft_no ,d.draft_start ,d.draft_end ,d.draft_subject ,e.name AS register ,(SELECT e2.name FROM approver a JOIN emp e2 WHERE d.draft_no = a.draft_no AND a.emp_no = e2.emp_no ORDER BY a.app_procedure DESC LIMIT 1) AS final_approver ,d.draft_status FROM draft d JOIN emp e ON d.register = e.emp_no WHERE emp_no = #{param4} and (d.draft_subject LIKE #{param1} OR e.name LIKE #{param1})
AND d.draft_status = #{param5}
ORDER BY d.draft_start DESC, d.draft_end ASC LIMIT #{param2}, #{param3}

SELECT 
	dr.draft_no
	,dr.draft_start 
	,dr.draft_end 
	,dr.draft_subject 
	,(select e.name from emp e where dr.register = e.emp_no) AS name
	,(select e2.name from approver a2 JOIN emp e2 WHERE dr.draft_no = a2.draft_no AND e2.emp_no = a2.emp_no ORDER BY a2.app_procedure desc limit 1) AS final_approver
	,dr.draft_status 
FROM draft dr JOIN approver ap 
	ON dr.draft_no = ap.draft_no 
	WHERE 
 (ap.emp_no = #{param4} 
 	AND dr.draft_status IN ('Y','N') 
	AND dr.draft_subject LIKE #{param1}
	AND dr.register != #{param4}
	AND ap.app_date is not null

	AND dr.draft_status = #{param5}
)
 	 OR 
 (ap.emp_no = #{param4}
	AND (SELECT a.app_date FROM approver a WHERE a.draft_no = dr.draft_no AND a.app_procedure = ap.app_procedure - 1) is not null
	AND dr.draft_status != 'T'
	AND ap.app_date is null
	AND dr.draft_subject LIKE #{param1}

	AND dr.draft_status = #{param5}
)
ORDER BY dr.draft_start DESC, dr.draft_end ASC LIMIT #{param2}, #{param3}

SELECT CEIL(COUNT(dr.draft_no)/#{param2}) AS pages 
	FROM draft dr JOIN approver ap 
		ON dr.draft_no = ap.draft_no 
		WHERE ap.emp_no = #{param3} AND (SELECT a.app_date FROM approver a WHERE a.draft_no = dr.draft_no AND a.app_procedure = ap.app_procedure - 1) is not null AND dr.draft_status != 'T'
		AND dr.draft_subject LIKE #{param1}

		AND draft_status = #{param4}

SELECT CEIL(COUNT(dr.draft_no)/#{param2}) AS pages FROM draft dr JOIN view_referrer vr ON dr.draft_no = vr.draft_no WHERE (vr.emp_no = #{param3} OR vr.dept_code = #{param5})
AND dr.draft_status = 'Y'
AND vr.view_reffer_division = #{param4} AND dr.draft_subject LIKE #{param1};

SELECT DISTINCT dr.draft_no ,dr.draft_start ,dr.draft_end ,dr.draft_subject ,(select e.name from emp e where dr.register = e.emp_no) AS name ,(select e2.name from approver a2 JOIN emp e2 WHERE dr.draft_no = a2.draft_no AND e2.emp_no = a2.emp_no ORDER BY a2.app_procedure desc limit 1) AS final_approver ,dr.draft_status FROM draft dr JOIN view_referrer vr ON dr.draft_no = vr.draft_no WHERE (vr.emp_no = #{param4} OR vr.dept_code = #{param6})
AND dr.draft_status = 'Y'
AND vr.view_reffer_division = #{param5} AND dr.draft_subject LIKE #{param1} ORDER BY dr.draft_start DESC, dr.draft_end ASC LIMIT #{param2}, #{param3}
