mapper.select:
SELECT distinct * FROM calendar c left JOIN calendar_share cs
ON cs.cal_no = c.cal_no
WHERE DATE(c.cal_start) BETWEEN #{param1} AND #{param2} AND c.emp_no = #{param3}

SELECT distinct * FROM calendar c left JOIN calendar_share cs
ON cs.cal_no = c.cal_no
WHERE DATE(cal_start) = #{param1} AND c.emp_no = #{param2}

SELECT distinct * FROM calendar c left JOIN calendar_share cs
ON cs.cal_no = c.cal_no
WHERE DATE(cal_start) = #{param1} AND c.emp_no = #{param2}
